// In App.js
import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from "./Header.js";
import Footer from "./Footer.js";
import FeedbackForm from "./FeedbackForm.js";
import Register from "./Register.js";
import Login from "./Login.js";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [users, setUsers] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState('');

  const handleLogin = (enteredUsername, enteredPassword) => {
    //Getting user list from local storage
    setUsers(localStorage.getItem('users'));
    // Check for authentication using the entered username and password
    const authenticatedUser = users.find(user => user.username === enteredUsername && user.password === enteredPassword);

    if (authenticatedUser) {
      setIsAuthenticated(true);
      setLoggedInUser(enteredUsername);
    } else {
      alert('Invalid username or password. Please try again.');
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setLoggedInUser('');
  };

  return (
    <div>
      <Header />

      {/* Define routes */}
<Routes>
  <Route path="/register" element={<Register onAuthentication={setUsers} />} />
  <Route
    path="/"
    element={
      isAuthenticated ? (
        <FeedbackForm username={loggedInUser} onLogout={handleLogout} />
      ) : (
        <Login onLogin={handleLogin} />
      )
    }
  />
</Routes>


      <Footer />
    </div>
  );
}

export default App;
