import { Route, Routes } from "react-router-dom";
import React, { useState } from 'react';
import Header from "./Header.js";
import Footer from "./Footer.js";
import FeedbackForm from "./FeedbackForm.js";
import Register from "./Register.js";
import Login from "./Login.js";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState('');

  const handleAuthentication = (user) => {
    // You would typically perform actual authentication here
    // For simplicity, we'll just set isAuthenticated to true for any user.
    setIsAuthenticated(true);
    setLoggedInUser(user);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setLoggedInUser('');
  };

  return (
    <div>
      <Header />

      {/* Define routes */}
      <Routes>
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <FeedbackForm username={loggedInUser} onLogout={handleLogout} />
            ) : (
              <Login onLogin={handleAuthentication} />
            )
          }
        />
        <Route path="/register" element={<Register onAuthentication={handleAuthentication} />} />
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
