import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Perform actual login authentication here
    // For simplicity, we'll check for a hardcoded username and password
    const validUsername = 'user1';
    const validPassword = 'password1';

    if (username === validUsername && password === validPassword) {
      // Notify the parent component that login is successful
      onLogin(username);
    } else {
      alert('Invalid username or password. Please try again.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="button" onClick={handleLogin}>
          Login
        </button>
      </form>

      {/* Link to navigate to the Register component */}
      <p>
        Don't have an account? <Link to="/register">Create one here</Link>.
      </p>
    </div>
  );
};

export default Login;
