// FormItem.js
import React from 'react';

const FormItem = ({ label, stateName, type, formData, inputHandler }) => {
  return (
    <div className="mb-3">
      <label htmlFor={stateName} className="form-label">
        {label}
      </label>
      {type === 'text' ? (
        <input
          type={type}
          className="form-control"
          id={stateName}
          name={stateName}
          value={formData[stateName]}
          onChange={(e) => inputHandler(e, stateName)}
        />
      ) : type === 'date' ? (
        <input
          type={type}
          className="form-control"
          id={stateName}
          name={stateName}
          value={formData[stateName]}
          onChange={(e) => inputHandler(e, stateName)}
        />
      ) : (
        <select
          className="form-control"
          id={stateName}
          name={stateName}
          value={formData[stateName]}
          onChange={(e) => inputHandler(e, stateName)}
          
        >
          <option value="" disabled>
            Select a course
          </option>
          <option value="Course 1">Course 1</option>
          <option value="Course 2">Course 2</option>
          {/* Add more options as needed */}
        </select>
      )}
    </div>
  );
};

export default FormItem;
